CREATE FUNCTION  [dbo].[AcountCheck] (@Amount bigint)   
returns float    
as begin   
declare    @appmstregno varchar(20)  
 Declare @L bigint , @R bigint, @W bigint   
    
 if @Amount=25  
 begin  
 set @W=     25  
 end   
 else  
 begin  
 set @R=    @Amount % 50     
 if @R=0  
 begin  
 set @W=@Amount  
 end  
 else   
 begin  
 set @W=0  
 end  
  
 end  
  
  return     @W  
end  
