			dbo.AppMst.AppMstID = dbo.DiamondMst.Appmstid where dbo.DiamondMst.sponsard=@SRno))
				if(@PairCount3=64)
				begin
				exec DiamondIncomeTreeVP @appmstid, 32000 , @DiamondID 
				End

END
