						@TranRemark,1,0,0,@Reason,@Tranid)                                                                              
						set @flag='1';                                                   
						commit transaction                                                        
					End	
				end                                                                                         
				else                                            
				set @flag='Insufficient IP balance.'; --low amount                                                                                         
			end                                                                        
			else                                                               
			set @flag='Invalid Transaction Password.';  -- E pwd invalid                                        
		end           
		else                                                        
		set @flag='Member not exists.';                                                         
	end                                                                                 
	                                       
end                                                        
