	set xact_abort on  
	begin transaction 
	begin try  
	select @appid=appmstid from appmst where AppMstRegNo =@regno  
	select @adminID=srno from ControlMst where username=@admin  
	select @amt =amount,@ReDate =DOE,@receiptNo=receiptno from Sharemst where srno=@srno
